"use client";

import { useState, useRef, useEffect } from 'react';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { apiFormData } from '@/lib/api';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { useToast } from '@/hooks/use-toast';
import { Camera, Upload, Tag, Users, Scan, Brain, Check, Eye } from 'lucide-react';
import { processImageForRecognition, findMatches, type DetectedObject, type ObjectSignature } from '@/lib/objectDetection';

export default function Identify() {
  const [capturedPhoto, setCapturedPhoto] = useState<string | null>(null);
  const [photoFile, setPhotoFile] = useState<File | null>(null);
  const [isCapturing, setIsCapturing] = useState(false);
  const [tags, setTags] = useState('');
  const [linkedContactId, setLinkedContactId] = useState<string | undefined>(undefined);
  const [notes, setNotes] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [detectedObjects, setDetectedObjects] = useState<DetectedObject[]>([]);
  const [objectSignature, setObjectSignature] = useState<ObjectSignature | null>(null);
  const [matchedObjects, setMatchedObjects] = useState<Array<{ id: number; userTag: string; confidence: number }>>([]);
  const [modelLoading, setModelLoading] = useState(false);
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const imageRef = useRef<HTMLImageElement>(null);
  const streamRef = useRef<MediaStream | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: contacts = [] } = useQuery({
    queryKey: ['contacts'],
    queryFn: async () => {
      const response = await fetch('/api/contacts');
      if (!response.ok) throw new Error('Failed to fetch contacts');
      return response.json();
    },
  });

  // Query for stored object recognitions
  const { data: storedObjects = [] } = useQuery({
    queryKey: ['object-recognitions'],
    queryFn: async () => {
      const response = await fetch('/api/identify/objects');
      if (!response.ok) throw new Error('Failed to fetch objects');
      return response.json();
    },
  });

  const uploadMutation = useMutation({
    mutationFn: (formData: FormData) => apiFormData('/api/identify', formData),
    onSuccess: () => {
      toast({ 
        title: 'Photo uploaded successfully',
        description: 'Photo has been tagged and saved'
      });
      queryClient.invalidateQueries({ queryKey: ['object-recognitions'] });
      resetForm();
    },
  });

  // --- CAMERA FUNCTIONS ---
  const startCamera = async () => {
    try {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
        streamRef.current = null;
      }

      const stream = await navigator.mediaDevices.getUserMedia({ video: { facingMode: 'environment' } });
      streamRef.current = stream;

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setTimeout(() => {
          videoRef.current?.play().catch(() => {
            console.warn('Autoplay prevented; user interaction needed');
          });
        }, 100);
      }

      setIsCapturing(true);
    } catch {
      toast({
        title: 'Camera error',
        description: 'Could not access camera. Check permissions.',
        variant: 'destructive',
      });
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    if (videoRef.current) videoRef.current.srcObject = null;
    setIsCapturing(false);
  };

  const capturePhoto = () => {
    if (!videoRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const video = videoRef.current;
    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

    canvas.toBlob(blob => {
      if (blob) {
        const file = new File([blob], 'captured-photo.jpg', { type: 'image/jpeg' });
        setPhotoFile(file);
        setCapturedPhoto(URL.createObjectURL(blob));

        // Automatically analyze the captured photo
        analyzePhoto(canvas);
      }
    }, 'image/jpeg', 0.8);

    stopCamera();
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setPhotoFile(file);
      const photoUrl = URL.createObjectURL(file);
      setCapturedPhoto(photoUrl);

      // Analyze uploaded photo
      const img = new Image();
      img.onload = () => analyzePhoto(img);
      img.src = photoUrl;
    }
  };

  // Object recognition analysis
  const analyzePhoto = async (imageElement: HTMLImageElement | HTMLCanvasElement) => {
    setIsAnalyzing(true);
    setModelLoading(true);

    try {
      const { detectedObjects, signature, visualFeatures } = await processImageForRecognition(imageElement);

      setDetectedObjects(detectedObjects);
      setObjectSignature(signature);
      setModelLoading(false);

      // Find matches with stored objects
      if (storedObjects.length > 0) {
        const storedSignatures = storedObjects.map((obj: any) => ({
          id: obj.id,
          signature: JSON.parse(obj.visual_features),
          userTag: obj.user_tag
        }));

        const matches = findMatches(signature, storedSignatures);
        setMatchedObjects(matches);

        if (matches.length > 0) {
          toast({
            title: 'Object recognized!',
            description: `Found ${matches.length} similar object(s) from your history`,
          });
        }
      }

    } catch (error) {
      console.error('Object analysis failed:', error);
      toast({
        title: 'Analysis failed',
        description: 'Could not analyze the photo. Please try again.',
        variant: 'destructive'
      });
    } finally {
      setIsAnalyzing(false);
      setModelLoading(false);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!photoFile) {
      toast({
        title: 'No photo selected',
        description: 'Please capture or upload a photo first',
        variant: 'destructive',
      });
      return;
    }

    const formData = new FormData();
    formData.append('photo', photoFile);
    formData.append('tags', tags);
    if (linkedContactId) formData.append('linked_contact_id', linkedContactId);
    formData.append('notes', notes);

    // Include object recognition data if available
    if (detectedObjects.length > 0) {
      formData.append('detected_objects', JSON.stringify(detectedObjects));
    }
    if (objectSignature) {
      formData.append('visual_features', JSON.stringify(objectSignature));
    }

    uploadMutation.mutate(formData);
  };

  const resetForm = () => {
    setCapturedPhoto(null);
    setPhotoFile(null);
    setTags('');
    setLinkedContactId(undefined);
    setNotes('');
    setDetectedObjects([]);
    setObjectSignature(null);
    setMatchedObjects([]);
    setIsAnalyzing(false);
    stopCamera();
  };

  return (
    <div className="p-8">
      <h2 className="text-4xl font-semibold mb-4">Identify & Tag</h2>
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Camera & Upload */}
        <Card>
          <CardHeader><CardTitle>Capture / Upload Photo</CardTitle></CardHeader>
          <CardContent className="space-y-4">
            {!capturedPhoto && !isCapturing && (
              <div className="text-center py-8 border-2 border-dashed rounded-lg">
                <Camera className="w-16 h-16 mx-auto mb-4 text-muted-foreground"/>
                <div className="flex justify-center gap-4">
                  <Button onClick={startCamera}><Camera className="w-4 h-4 mr-2"/>Use Camera</Button>
                  <Label htmlFor="photo-upload" className="cursor-pointer">
                    <Button variant="outline" asChild>
                      <span><Upload className="w-4 h-4 mr-2"/>Upload Photo</span>
                    </Button>
                  </Label>
                  <Input id="photo-upload" type="file" accept="image/*" className="hidden" onChange={handleFileUpload}/>
                </div>
              </div>
            )}

            {isCapturing && (
              <div className="relative">
                <video ref={videoRef} autoPlay playsInline className="w-full rounded-lg"/>
                <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 flex space-x-4">
                  <Button onClick={capturePhoto} size="lg" className="rounded-full h-16 w-16"><Camera className="w-6 h-6"/></Button>
                  <Button onClick={stopCamera} variant="outline">Cancel</Button>
                </div>
              </div>
            )}

            {capturedPhoto && (
              <div className="relative">
                <img ref={imageRef} src={capturedPhoto} alt="Captured" className="w-full rounded-lg"/>
                <Button onClick={resetForm} variant="outline" className="absolute top-2 right-2">Retake</Button>

                {(isAnalyzing || modelLoading) && (
                  <div className="absolute inset-0 bg-black/50 flex items-center justify-center rounded-lg">
                    <div className="text-white text-center">
                      <Brain className="w-8 h-8 mx-auto mb-2 animate-pulse"/>
                      <p>{modelLoading ? 'Loading AI model...' : 'Analyzing objects...'}</p>
                    </div>
                  </div>
                )}
              </div>
            )}

            <canvas ref={canvasRef} className="hidden"/>
          </CardContent>
        </Card>

        {/* Analysis Results & Tagging */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Eye className="w-5 h-5"/>
              Tag & Identify
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">

            {/* Object Recognition Results */}
            {detectedObjects.length > 0 && (
              <div className="space-y-3">
                <Label className="text-sm font-medium">Detected Objects</Label>
                <div className="flex flex-wrap gap-2">
                  {detectedObjects.map((obj, index) => (
                    <Badge key={index} variant="secondary" className="flex items-center gap-1">
                      <Scan className="w-3 h-3"/>
                      {obj.class} ({Math.round(obj.score * 100)}%)
                    </Badge>
                  ))}
                </div>
              </div>
            )}

            {/* Matched Objects */}
            {matchedObjects.length > 0 && (
              <Alert>
                <Check className="w-4 h-4"/>
                <AlertDescription>
                  <strong>Object recognized!</strong> Found {matchedObjects.length} similar item(s):
                  <div className="mt-2 space-y-1">
                    {matchedObjects.map((match, index) => (
                      <div key={index} className="flex items-center justify-between">
                        <span className="font-medium">"{match.userTag}"</span>
                        <Badge variant="outline">{Math.round(match.confidence * 100)}% match</Badge>
                      </div>
                    ))}
                  </div>
                </AlertDescription>
              </Alert>
            )}

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label>Your Tag/Description</Label>
                <Input 
                  value={tags} 
                  onChange={(e) => setTags(e.target.value)} 
                  placeholder={detectedObjects.length > 0 ? 
                    `Describe this ${detectedObjects[0]?.class || 'object'}...` : 
                    "Describe what you see..."
                  }
                />
                {matchedObjects.length > 0 && (
                  <div className="mt-2">
                    <p className="text-sm text-muted-foreground mb-1">Quick fill from matches:</p>
                    <div className="flex flex-wrap gap-1">
                      {matchedObjects.slice(0, 3).map((match, index) => (
                        <Button
                          key={index}
                          type="button"
                          variant="outline"
                          size="sm"
                          onClick={() => setTags(match.userTag)}
                          className="text-xs"
                        >
                          {match.userTag}
                        </Button>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              <div>
                <Label>Link to Contact</Label>
                <Select value={linkedContactId} onValueChange={setLinkedContactId}>
                  <SelectTrigger><SelectValue placeholder="Select a person (optional)"/></SelectTrigger>
                  <SelectContent>
                    {contacts.map((contact: any) => (
                      <SelectItem key={contact.id} value={contact.id.toString()}>
                        {contact.name} ({contact.relation})
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label>Notes</Label>
                <Input value={notes} onChange={(e) => setNotes(e.target.value)} placeholder="Additional notes"/>
              </div>

              <Button type="submit" className="w-full" disabled={!photoFile || uploadMutation.isPending}>
                {uploadMutation.isPending ? 'Saving...' : 
                 detectedObjects.length > 0 ? 'Save Identified Object' : 'Save Photo & Tags'}
              </Button>
            </form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/Journal.tsx.content:
import { useState, useRef } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { journalApi } from '@/lib/api';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Textarea } from '@/components/ui/textarea';
import { useToast } from '@/hooks/use-toast';
import { Plus, Keyboard, Mic, Play, OctagonMinus, Pause, Download, Edit, Trash2 } from 'lucide-react';

export default function Journal() {
  const [entryType, setEntryType] = useState<'text' | 'voice'>('text');
  const [textContent, setTextContent] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [transcription, setTranscription] = useState('');
  const [audioBlob, setAudioBlob] = useState<Blob | null>(null);
  const mediaRecorderRef = useRef<MediaRecorder | null>(null);
  const recognitionRef = useRef<any>(null);
  const [currentlyPlaying, setCurrentlyPlaying] = useState<number | null>(null);
  const audioRefs = useRef<{ [key: number]: HTMLAudioElement }>({});
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: journalEntries = [], isLoading } = useQuery({
    queryKey: ['journal'],
    queryFn: journalApi.list,
  });

  const createMutation = useMutation({
    mutationFn: (formData: FormData) => journalApi.create(formData),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['journal'] });
      setTextContent('');
      setTranscription('');
      setAudioBlob(null);
      toast({ title: 'Journal entry saved successfully' });
    },
  });

  const startVoiceRecording = async () => {
    try {
      // Start audio recording
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      const chunks: BlobPart[] = [];

      mediaRecorder.ondataavailable = (e) => chunks.push(e.data);
      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        setAudioBlob(blob);
      };

      mediaRecorderRef.current = mediaRecorder;
      mediaRecorder.start();

      // Start speech recognition
      if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
        const SpeechRecognition = (window as any).webkitSpeechRecognition || (window as any).SpeechRecognition;
        const recognition = new SpeechRecognition();

        recognition.continuous = true;
        recognition.interimResults = true;
        recognition.lang = 'en-US';

        recognition.onresult = (event: any) => {
          let finalTranscript = '';
          for (let i = event.resultIndex; i < event.results.length; i++) {
            if (event.results[i].isFinal) {
              finalTranscript += event.results[i][0].transcript;
            }
          }
          if (finalTranscript) {
            setTranscription(prev => prev + finalTranscript + ' ');
          }
        };

        recognition.onerror = (event: any) => {
          console.error('Speech recognition error:', event.error);
        };

        recognitionRef.current = recognition;
        recognition.start();
      }

      setIsRecording(true);
    } catch (error) {
      toast({
        title: 'Recording failed',
        description: 'Could not access microphone',
        variant: 'destructive',
      });
    }
  };

  const stopVoiceRecording = () => {
    if (mediaRecorderRef.current) {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
    }

    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }

    setIsRecording(false);
  };

  const playPreviewAudio = () => {
    if (audioBlob) {
      const audio = new Audio(URL.createObjectURL(audioBlob));
      audio.play();
    }
  };

  const playAudio = (entryId: number, audioPath: string) => {
    if (currentlyPlaying === entryId) {
      // Stop current audio
      if (audioRefs.current[entryId]) {
        audioRefs.current[entryId].pause();
        audioRefs.current[entryId].currentTime = 0;
      }
      setCurrentlyPlaying(null);
      return;
    }

    // Stop any currently playing audio
    if (currentlyPlaying && audioRefs.current[currentlyPlaying]) {
      audioRefs.current[currentlyPlaying].pause();
      audioRefs.current[currentlyPlaying].currentTime = 0;
    }

    // Create and play new audio
    if (!audioRefs.current[entryId]) {
      audioRefs.current[entryId] = new Audio(audioPath);
      audioRefs.current[entryId].addEventListener('ended', () => {
        setCurrentlyPlaying(null);
      });
    }

    audioRefs.current[entryId].play();
    setCurrentlyPlaying(entryId);
  };

  const downloadAudio = (audioPath: string, entryId: number) => {
    const link = document.createElement('a');
    link.href = audioPath;
    link.download = `journal-entry-${entryId}.wav`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const saveEntry = () => {
    const formData = new FormData();

    if (entryType === 'text') {
      if (!textContent.trim()) {
        toast({
          title: 'No content',
          description: 'Please enter some text before saving',
          variant: 'destructive',
        });
        return;
      }
      formData.append('type', 'text');
      formData.append('content_text', textContent);
    } else {
      if (!audioBlob || !transcription.trim()) {
        toast({
          title: 'No content',
          description: 'Please record audio before saving',
          variant: 'destructive',
        });
        return;
      }
      formData.append('type', 'audio');
      formData.append('content_text', transcription);
      formData.append('audio', audioBlob, 'journal-entry.wav');
    }

    createMutation.mutate(formData);
  };

  if (isLoading) {
    return <div className="p-8">Loading journal entries...</div>;
  }

  return (
    <div className="p-8">
      <div className="mb-8 flex items-center justify-between">
        <div>
          <h2 className="text-4xl font-semibold text-foreground mb-2" data-testid="journal-title">
            Personal Journal
          </h2>
          <p className="text-xl text-muted-foreground">
            Record your thoughts, memories, and daily experiences
          </p>
        </div>
      </div>

      {/* New Entry Form */}
      <Card className="mb-8" data-testid="card-new-entry">
        <CardHeader>
          <CardTitle>Write New Entry</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {/* Entry Type Selection */}
          <div>
            <label className="block text-lg font-medium text-card-foreground mb-2">Entry Type</label>
            <div className="flex space-x-4">
              <Button
                variant={entryType === 'text' ? 'default' : 'outline'}
                className="flex-1 p-4 h-auto flex flex-col space-y-2"
                onClick={() => setEntryType('text')}
                data-testid="button-text-entry"
              >
                <Keyboard className="text-2xl" />
                <div>Text Entry</div>
              </Button>
              <Button
                variant={entryType === 'voice' ? 'default' : 'outline'}
                className="flex-1 p-4 h-auto flex flex-col space-y-2"
                onClick={() => setEntryType('voice')}
                data-testid="button-voice-entry"
              >
                <Mic className="text-2xl" />
                <div>Voice Entry</div>
              </Button>
            </div>
          </div>

          {/* Text Entry Area */}
          {entryType === 'text' && (
            <div>
              <label className="block text-lg font-medium text-card-foreground mb-2">Your thoughts</label>
              <Textarea
                value={textContent}
                onChange={(e) => setTextContent(e.target.value)}
                placeholder="What would you like to remember about today?"
                className="h-40 text-lg resize-none"
                data-testid="textarea-journal-content"
              />
            </div>
          )}

          {/* Voice Recording Controls */}
          {entryType === 'voice' && (
            <div className="space-y-4">
              {!isRecording && !audioBlob && (
                <div className="text-center py-8">
                  <Button
                    size="lg"
                    onClick={startVoiceRecording}
                    className="h-20 w-20 rounded-full"
                    data-testid="button-start-recording"
                  >
                    <Mic className="text-2xl" />
                  </Button>
                  <p className="mt-4 text-muted-foreground">Tap to start recording</p>
                </div>
              )}

              {isRecording && (
                <div className="bg-muted p-6 rounded-lg text-center">
                  <div className="mb-4">
                    <div className="w-16 h-16 bg-destructive rounded-full mx-auto flex items-center justify-center animate-pulse">
                      <Mic className="text-2xl text-destructive-foreground" />
                    </div>
                  </div>
                  <p className="text-lg text-card-foreground mb-4">Recording... Speak clearly</p>
                  <Button
                    onClick={stopVoiceRecording}
                    variant="destructive"
                    data-testid="button-stop-recording"
                  >
                    <OctagonMinus className="w-4 h-4 mr-2" />
                    OctagonMinus Recording
                  </Button>
                  {transcription && (
                    <div className="mt-4 p-4 bg-background rounded border">
                      <p className="text-sm text-muted-foreground mb-2">Live transcription:</p>
                      <p className="text-card-foreground">{transcription}</p>
                    </div>
                  )}
                </div>
              )}

              {audioBlob && !isRecording && (
                <div className="space-y-4">
                  <div className="bg-muted p-4 rounded-lg">
                    <p className="text-sm text-muted-foreground mb-2">Recording completed</p>
                    <div className="flex items-center space-x-4">
                      <Button size="sm" variant="outline" onClick={playPreviewAudio} data-testid="button-play-recording">
                        <Play className="w-4 h-4" />
                      </Button>
                      <div className="flex-1 bg-background rounded-full h-2">
                        <div className="bg-primary h-2 rounded-full w-0"></div>
                      </div>
                      <span className="text-sm text-muted-foreground">0:00</span>
                    </div>
                  </div>

                  {transcription && (
                    <div className="bg-background p-4 rounded-lg">
                      <h4 className="font-medium text-card-foreground mb-2">Transcription:</h4>
                      <Textarea
                        value={transcription}
                        onChange={(e) => setTranscription(e.target.value)}
                        className="min-h-[100px]"
                        data-testid="textarea-transcription"
                      />
                    </div>
                  )}
                </div>
              )}
            </div>
          )}

          <div className="flex space-x-4">
            <Button
              onClick={saveEntry}
              disabled={createMutation.isPending}
              data-testid="button-save-entry"
            >
              {createMutation.isPending ? 'Saving...' : 'Save Entry'}
            </Button>
            <Button
              variant="outline"
              onClick={() => {
                setTextContent('');
                setTranscription('');
                setAudioBlob(null);
                setEntryType('text');
              }}
              data-testid="button-clear-entry"
            >
              Clear
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Recent Entries */}
      <div className="space-y-6">
        <h3 className="text-2xl font-semibold text-foreground">Recent Entries</h3>

        {journalEntries.length === 0 ? (
          <Card>
            <CardContent className="py-8">
              <div className="text-center" data-testid="empty-journal">
                <Plus className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-xl font-semibold mb-2">No journal entries yet</h3>
                <p className="text-muted-foreground">Start writing your first entry above</p>
              </div>
            </CardContent>
          </Card>
        ) : (
          journalEntries.map((entry: any) => (
            <Card key={entry.id} data-testid={`journal-entry-${entry.id}`}>
              <CardContent className="p-6">
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center space-x-3">
                    {entry.type === 'text' ? (
                      <Keyboard className="text-primary" />
                    ) : (
                      <Mic className="text-accent" />
                    )}
                    <span className="text-lg font-medium text-card-foreground">
                      {entry.type === 'text' ? 'Text Entry' : 'Voice Entry'}
                    </span>
                    {entry.type === 'audio' && (
                      <span className="text-sm text-muted-foreground">(Audio recording)</span>
                    )}
                  </div>
                  <div className="text-muted-foreground">
                    {new Date(entry.created_at).toLocaleString()}
                  </div>
                </div>

                {entry.type === 'audio' && entry.audio_path && (
                  <div className="bg-muted p-4 rounded-lg mb-4">
                    <div className="flex items-center space-x-4">
                      <Button 
                        size="sm" 
                        onClick={() => playAudio(entry.id, entry.audio_path)}
                        data-testid={`button-play-${entry.id}`}
                      >
                        {currentlyPlaying === entry.id ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
                      </Button>
                      <div className="flex-1 bg-background rounded-full h-2">
                        <div className="bg-primary h-2 rounded-full" style={{ width: '30%' }}></div>
                      </div>
                      <div className="text-sm text-muted-foreground">1:30</div>
                      <Button 
                        size="sm" 
                        variant="outline" 
                        onClick={() => downloadAudio(entry.audio_path, entry.id)}
                        data-testid={`button-download-${entry.id}`}
                      >
                        <Download className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                )}

                {entry.content_text && (
                  <div className="mb-4">
                    {entry.type === 'audio' && (
                      <h4 className="font-medium text-card-foreground mb-2">Transcription:</h4>
                    )}
                    <p className="text-lg text-card-foreground leading-relaxed">
                      {entry.content_text}
                    </p>
                  </div>
                )}

                <div className="flex space-x-2">
                  <Button size="sm" variant="outline" data-testid={`button-edit-${entry.id}`}>
                    <Edit className="w-4 h-4 mr-1" />
                    Edit
                  </Button>
                  <Button size="sm" variant="outline" data-testid={`button-delete-${entry.id}`}>
                    <Trash2 className="w-4 h-4 mr-1" />
                    Delete
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

